package lec1;

public class AssignmentOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 5;
		x += 3;
		System.out.println(x);
		
		x -= 5;
		System.out.println(x);
		
		x*=2;
		System.out.println(x);
		
		x/=4;
		System.out.println(x);
		
		int y = 26;
		y %= 15;
		System.out.println(y);
	}

}
